// Calculate the heater output and assign it to msg.payload
const T = flow.get("temp"),
    DB = context.get("deadband"),
    Setpoint = context.get("thermostat"),
    Heater = Setpoint > T + DB ? 1 : 0;

// No output if heater" condition does not change.
if (context.get("heater") == Heater) { return };

