    }
];

return msg;

let msgHum = null, 
    msgPres = null,
    msgSLP = null,
    msgTemp = null,
    msgErr = null, 
    msgTime = null;

// Update variables with topic in context (elevation):
if (msg.topic == "elevation") { context.set("elevation", msg.payload); }

// Update BME readings (msg.payload is a JSON with one key per variable):
else { 
    const obj = msg.payload;
    msgTemp = { topic: "temp", payload: obj.temp / 10 };
    msgHum = { topic: "RH-%", payload: obj.hum / 10 };
    msgPres = { payload: obj.pres / 10 };
    msgTime = { payload: new Date().toLocaleString("es-ES") };
    msgErr = { payload: obj.err };

    context.set("temp", obj.temp / 10);
    context.set("pres", obj.pres / 10);
}

// Calculation of Sea Level Pressure:
// T = Temperature at station(in K — Kelvin)
// L = Temperature lapse rate ≈ 0.0065 K / m
// g = Gravitational acceleration ≈ 9.80665 m / s²
// M = Molar mass of dry air ≈ 0.0289644 kg / mol
// R = Universal gas constant ≈ 8.31432 N·m / (mol·K)
// exp = -(g * M)/(R * L)
let T = context.get("temp");
const P = context.get("pres");
const H = context.get("elevation");
if (T === undefined || P === undefined || H === undefined) {
    node.warn("Missing required inputs. Waiting for all inputs to be available.");
    return null; // Don't compute until all data is available
}

T += 273.15;
const L = 0.0065,
    exp = -5.255;

msgSLP = { topic: "Sea Level Press.", payload: Math.round(P * Math.pow(1- (L * H) / T, exp)) };

return [msgTemp, msgSLP, msgPres, msgHum, msgTime, msgErr];

// The msg received can be ot the types:
//   {"payload": 20, "topic": "thermostat"}
//   {"payload": 0.3, "topic": "deadband"}
//   {"payload": 21.4, "topic": "temp"}
// Topic = name of the context-scoped variable.
// Payload = new value for that variable.

// Update the corresponding context variable
// ("deadband" / "temp" / "thermostat")
context.set(msg.topic, msg.payload);

// Calculate the "heater" state.
const T = context.get("temp"),
    DB = context.get("deadband"),
    Setpoint = context.get("thermostat"),
    Heater = Setpoint > T + DB ? 1 : 0,
    HeaterChart = 4 * (Heater + .1);   // -> 0.4 (off) or 4.4 (on) in chart

node.status({
    fill: Heater == 1 ? "red" : "green",
    shape: "ring",
    text: Heater == 1 ? "Heater ON" : "Heater OFF"
});

// "heater" changed? → update context and send to websocket (output 1)
if (context.get("heater") !== Heater) {
    context.set("heater", Heater);
    return [
        { topic: "gpio/heater", payload: Heater },
        { text: "Heater " + (Heater === 1? "ON": "OFF") },
        { topic: "Heater", payload: HeaterChart },
        { topic: "Setpoint", payload: Setpoint },
        { topic: "Temp", payload: T, }
    ]
};

// Only deadband changed? → no output.
if (msg.topic == "deadband") return;

// No change in heater state → only send remaining outputs
return [
    null,
    null,
    { topic: "Heater", payload: HeaterChart },
    { topic: "Setpoint", payload: Setpoint },
    { topic: "Temp", payload: T, }
];

// Get all the network interfaces on the system:
const interfaces = os.networkInterfaces();
let localIP = '';

const preferredInterfaces = ['Wi-Fi', 'wlan0', 'eth0', 'Ethernet'];

for (const name of preferredInterfaces.concat(Object.keys(interfaces))) {
    const ifaceList = interfaces[name];
    if (!ifaceList) continue;

    for (const iface of ifaceList) {
        if (iface.family === 'IPv4' && !iface.internal) {
            localIP = iface.address;
            break;
        }
    }
    if (localIP) break;
}

msg.topic = "localIP";
msg.payload = localIP || "Not found";
return msg;
